var loginVM=new Vue({el:".login-form",data:{loading:!1,success:!1,btnSuccess:!1,btnError:!1,btnInterval:null},methods:{postData:function(){var n=this,t=n.$el.action||"",e=n.$el.method||"post";if(!n.loading){n.loading=!0;var o={url:t,method:e,data:$(n.$el).serialize()};$.ajax(o).done(function(){n.btnSuccess=!0,Materialize.toast("You've been logged in!",1500,"status success"),window.location.reload()}).fail(function(t){var e=t.responseJSON||{errors:["Something went wrong!"]},o=e.errors;n.btnError=!0;for(var r in o)o.hasOwnProperty(r)&&Materialize.toast(o[r],1500,"status error")}).always(function(){n.loading=!1,window.clearInterval(n.btnInterval),n.btnInterval=window.setTimeout(function(){n.btnSuccess=!1,n.btnError=!1},1500)})}}}});